import{s as H,n as g}from"./scheduler.63274e7e.js";import{S as q,i as S,g as c,s as f,h as p,x as m,c as u,a,f as o}from"./index.9f372ec1.js";function z(M){let i,I="I recently had to get a new job. My wife got a residency in another state, and my previous job is going back to in-person.",w,n,C="I have two years’ experience working for a startup on enterprise-scale software. My day job is on the frontend with some backend from open source work.",y,s,_="Here are some things I was asked to do while job hunting:",v,l,T="<li>Work as a contractor because the company forgot I said I’d be moving and didn’t want to file the paperwork in two states. That seemed reasonable, so I agreed. At the time, I did not know the school or state of my wife’s residency.[^1] The company waffled, then pulled the offer with no explanation. They did at least pay me for time spent on the technical screen.</li> <li>Fill out a recruiter’s profile of me for him (Word doc and internal assessment and all).</li> <li>Pass a cognitive test asking me to do basic arithmetic and convert fractions as fast as I could.</li> <li>Take a personality test where I had to select adjectives I felt applied to me.</li> <li>Enter my years of experience with “Problem Solving.” I am 28 years old, so I put 28.</li> <li>Listen to a recruiter tell me I could move to South Florida for a job after a year because “who knows if you and your girlfriend will still be together.” I had referred to her as my wife on the same call.</li>",b,r,P="The cognitive test actually made me laugh. Imagine a workplace where someone yells, “Nazario! Get over here, we need someone add some fractions! No calculators!!!”",x,d,j='Anyway, hiring is broken, we all know it. I accepted a position with a <a href="https://www.bitovi.com" rel="nofollow">company</a> that had a sane, speedy hiring process.',k,h,L="[^1]: Veterinarians can do a residency if they want to specialize, like to be a neurologist or cardiologist. You get most residencies through “the match.” Prospective residents rank programs they’re most interested in, and the programs rank applicants. On a given date in the spring, you find out if or where you matched.<br/><br/>This is why for several weeks, I knew we would be in Tennessee, Florida, Indiana or California, but not the specific state.";return{c(){i=c("p"),i.textContent=I,w=f(),n=c("p"),n.textContent=C,y=f(),s=c("p"),s.textContent=_,v=f(),l=c("ul"),l.innerHTML=T,b=f(),r=c("p"),r.textContent=P,x=f(),d=c("p"),d.innerHTML=j,k=f(),h=c("p"),h.innerHTML=L},l(e){i=p(e,"P",{"data-svelte-h":!0}),m(i)!=="svelte-135dmhu"&&(i.textContent=I),w=u(e),n=p(e,"P",{"data-svelte-h":!0}),m(n)!=="svelte-7aqmiz"&&(n.textContent=C),y=u(e),s=p(e,"P",{"data-svelte-h":!0}),m(s)!=="svelte-cqekum"&&(s.textContent=_),v=u(e),l=p(e,"UL",{"data-svelte-h":!0}),m(l)!=="svelte-17xy20f"&&(l.innerHTML=T),b=u(e),r=p(e,"P",{"data-svelte-h":!0}),m(r)!=="svelte-1y90nxx"&&(r.textContent=P),x=u(e),d=p(e,"P",{"data-svelte-h":!0}),m(d)!=="svelte-cmk077"&&(d.innerHTML=j),k=u(e),h=p(e,"P",{"data-svelte-h":!0}),m(h)!=="svelte-17hsqwr"&&(h.innerHTML=L)},m(e,t){a(e,i,t),a(e,w,t),a(e,n,t),a(e,y,t),a(e,s,t),a(e,v,t),a(e,l,t),a(e,b,t),a(e,r,t),a(e,x,t),a(e,d,t),a(e,k,t),a(e,h,t)},p:g,i:g,o:g,d(e){e&&(o(i),o(w),o(n),o(y),o(s),o(v),o(l),o(b),o(r),o(x),o(d),o(k),o(h))}}}const N={layout:"post",title:"Things I was asked to do while job hunting",date:"2021-04-10T00:00:00.000Z",keywords:"jobs, career, personal",hn:"https://news.ycombinator.com/item?id=26763336"};class W extends q{constructor(i){super(),S(this,i,null,z,H,{})}}export{W as default,N as metadata};
